export class EncryptionService {
  private key: CryptoKey | null = null;

  // ä»é’±åŒ…ç­¾åæ´¾ç”Ÿå¯†é’¥
  async deriveKeyFromWallet(signMessage: (msg: string) => Promise<string>) {
    // 1. ç”¨æˆ·ç­¾åå›ºå®šæ¶ˆæ¯
    const signature = await signMessage("mindvault-encryption-key-v1");

    // 2. å°†ç­¾åè½¬ä¸ºå¯†é’¥ææ–™
    const keyMaterial = await crypto.subtle.importKey(
      "raw",
      new TextEncoder().encode(signature),
      "HKDF",
