    // 3. ä½¿ç”¨ HKDF æ´¾ç”Ÿ AES-256 å¯†é’¥
    this.key = await crypto.subtle.deriveKey(
      {
        name: "HKDF",
        hash: "SHA-256",
        salt: new TextEncoder().encode("mindvault-salt"),
        info: new TextEncoder().encode("aes-key"),
      },
      keyMaterial,
      { name: "AES-GCM", length: 256 },
      false,
      ["encrypt", "decrypt"]
    );
  }

  // AES-256-GCM åŠ å¯†
  async encrypt(plaintext: string): Promise<{ ciphertext: Uint8Array; iv: Uint8Array }> {
    const iv = crypto.getRandomValues(new Uint8Array(12));
    const encoded = new TextEncoder().encode(plaintext);

    const ciphertext = await crypto.subtle.encrypt(
      { name: "AES-GCM", iv },
      this.key!,
      encoded
    );

    return { ciphertext: new Uint8Array(ciphertext), iv };
  }

  // AES-256-GCM è§£å¯†
  async decrypt(ciphertext: Uint8Array, iv: Uint8Array): Promise<string> {
    const decrypted = await crypto.subtle.decrypt(
      { name: "AES-GCM", iv },
      this.key!,
      ciphertext
    );

    return new TextDecoder().decode(decrypted);
  }
}
