     1â†’/**
     2â†’ * MindVault AI Chat API Route
     3â†’ *
     4â†’ * Stateless Enclave - No data persistence
     5â†’ * 1. READ: Receive decrypted plaintext from frontend
     6â†’ * 2. PROCESS: Call LLM for inference
     7â†’ * 3. FORGET: Request ends, memory released, no disk persistence
     8â†’ *
     9â†’ * Supports custom AI providers via OpenAI-compatible protocol.
    10â†’ * User provides their own baseURL, apiKey, and model.
    11â†’ */
    12â†’
    13â†’import { NextRequest, NextResponse } from "next/server";
    14â†’import OpenAI from "openai";
    15â†’
    16â†’interface ChatMessage {
    17â†’  role: "user" | "assistant";
    18â†’  content: string;
    19â†’}
    20â†’
    21â†’interface AIProviderConfig {
    22â†’  baseURL: string;
    23â†’  apiKey: string;
    24â†’  model: string;
    25â†’}
    26â†’
    27â†’interface ChatRequest {
    28â†’  message: string;
    29â†’  provider: AIProviderConfig;
    30â†’  history?: ChatMessage[];
    31â†’}
    32â†’
    33â†’/**
    34â†’ * Call any OpenAI-compatible API
    35â†’ * Most AI providers (OpenAI, DeepSeek, Moonshot, local Ollama) use this format
    36â†’ */
    37â†’async function callOpenAICompatible(
    38â†’  config: AIProviderConfig,
    39â†’  message: string,
    40â†’  history: ChatMessage[] = []
    41â†’): Promise<string> {
    42â†’  const client = new OpenAI({
    43â†’    baseURL: config.baseURL,
    44â†’    apiKey: config.apiKey,
    45â†’  });
    46â†’
    47â†’  const messages: OpenAI.Chat.ChatCompletionMessageParam[] = [
    48â†’    {
    49â†’      role: "system",
    50â†’      content: "You are a helpful AI assistant. Be concise and helpful.",
    51â†’    },
    52â†’    ...history.map((m) => ({
    53â†’      role: m.role as "user" | "assistant",
    54â†’      content: m.content,
    55â†’    })),
    56â†’    { role: "user", content: message },
    57â†’  ];
    58â†’
    59â†’  const completion = await client.chat.completions.create({
    60â†’    model: config.model,
    61â†’    messages,
    62â†’    max_tokens: 2048,
    63â†’  });
    64â†’
    65â†’  return completion.choices[0]?.message?.content || "No response generated.";
    66â†’}
    67â†’
    68â†’/**
    69â†’ * Call Anthropic Claude API (different format from OpenAI)
    70â†’ */
    71â†’async function callClaude(
    72â†’  config: AIProviderConfig,
    73â†’  message: string,
    74â†’  history: ChatMessage[] = []
    75â†’): Promise<string> {
    76â†’  const messages = [
    77â†’    ...history.map((m) => ({
    78â†’      role: m.role,
    79â†’      content: m.content,
    80â†’    })),
    81â†’    { role: "user", content: message },
    82â†’  ];
    83â†’
    84â†’  const response = await fetch(`${config.baseURL}/messages`, {
    85â†’    method: "POST",
    86â†’    headers: {
    87â†’      "Content-Type": "application/json",
    88â†’      "x-api-key": config.apiKey,
    89â†’      "anthropic-version": "2023-06-01",
    90â†’    },
    91â†’    body: JSON.stringify({
    92â†’      model: config.model,
    93â†’      max_tokens: 2048,
    94â†’      messages,
    95â†’    }),
    96â†’  });
    97â†’
    98â†’  if (!response.ok) {
    99â†’    const error = await response.text();
   100â†’    throw new Error(`Claude API error: ${error}`);
   101â†’  }
   102â†’
   103â†’  const data = await response.json();
   104â†’  return data.content[0]?.text || "No response generated.";
   105â†’}
   106â†’
   107â†’/**
   108â†’ * Detect if this is an Anthropic Claude endpoint
   109â†’ */
   110â†’function isClaudeEndpoint(baseURL: string): boolean {
   111â†’  return baseURL.includes("anthropic.com");
   112â†’}
   113â†’
   114â†’export async function POST(request: NextRequest) {
   115â†’  try {
   116â†’    // 1. READ: Parse request body
   117â†’    const body: ChatRequest = await request.json();
   118â†’    const { message, provider, history = [] } = body;
   119â†’
   120â†’    if (!message) {
   121â†’      return NextResponse.json(
   122â†’        { error: "Message is required" },
   123â†’        { status: 400 }
   124â†’      );
   125â†’    }
   126â†’
   127â†’    if (!provider || !provider.baseURL || !provider.apiKey || !provider.model) {
   128â†’      return NextResponse.json(
   129â†’        { error: "AI provider configuration is required (baseURL, apiKey, model)" },
   130â†’        { status: 400 }
   131â†’      );
   132â†’    }
   133â†’
   134â†’    // 2. PROCESS: Call appropriate LLM based on endpoint type
   135â†’    let response: string;
   136â†’
   137â†’    if (isClaudeEndpoint(provider.baseURL)) {
   138â†’      // Use Anthropic-specific API format
   139â†’      response = await callClaude(provider, message, history);
   140â†’    } else {
   141â†’      // Use OpenAI-compatible format (works for OpenAI, DeepSeek, Moonshot, Ollama, etc.)
   142â†’      response = await callOpenAICompatible(provider, message, history);
   143â†’    }
   144â†’
   145â†’    // 3. FORGET: Return response
   146â†’    // After this function returns, all local variables (message, response, history, provider)
   147â†’    // are garbage collected. Serverless functions have no persistent storage.
   148â†’    // No database writes, no log persistence, no cache storage.
   149â†’    // The API key is used only for this request and never stored.
   150â†’
   151â†’    return NextResponse.json({ response });
   152â†’  } catch (error) {
   153â†’    console.error("Chat API error:", error);
   154â†’
   155â†’    const errorMessage =
   156â†’      error instanceof Error ? error.message : "An error occurred";
   157â†’
   158â†’    return NextResponse.json(
   159â†’      { error: errorMessage },
   160â†’      { status: 500 }
   161â†’    );
   162â†’  }
   163â†’}
   164â†’
   165â†’// Disable body parsing for streaming (future enhancement)
   166â†’export const runtime = "nodejs";
   167â†’

