     3â†’import { useState, useCallback, useEffect } from "react";
     4â†’import { useCurrentAccount, useSignAndExecuteTransaction } from "@mysten/dapp-kit";
     5â†’import {
     6â†’  buildCreateVaultTx,
     7â†’  buildStoreBlobTx,
     8â†’  buildGrantAccessTx,
     9â†’  buildRevokeAccessTx,
    10â†’  getUserVaults,
    11â†’  getVaultDetails,
    12â†’} from "@/lib/sui";
    13â†’
    14â†’export interface VaultInfo {
    15â†’  objectId: string;
    16â†’  blobCount: number;
    17â†’  createdAt: number;
    18â†’  owner?: string;
    19â†’}
    20â†’
    21â†’export function useVault() {
    22â†’  const [vaults, setVaults] = useState<VaultInfo[]>([]);
    23â†’  const [currentVault, setCurrentVault] = useState<VaultInfo | null>(null);
    24â†’  const [isLoading, setIsLoading] = useState(false);
    25â†’  const [error, setError] = useState<string | null>(null);
    26â†’
    27â†’  const account = useCurrentAccount();
    28â†’  const { mutateAsync: signAndExecute } = useSignAndExecuteTransaction();
    29â†’
    30â†’  // Fetch user's vaults
    31â†’  const fetchVaults = useCallback(async () => {
    32â†’    if (!account?.address) return;
    33â†’
    34â†’    setIsLoading(true);
    35â†’    setError(null);
    36â†’
    37â†’    try {
    38â†’      const userVaults = await getUserVaults(account.address);
    39â†’      setVaults(userVaults);
    40â†’
    41â†’      // Set current vault to the first one if not set
    42â†’      if (userVaults.length > 0 && !currentVault) {
    43â†’        setCurrentVault(userVaults[0]);
    44â†’      }
    45â†’    } catch (err) {
    46â†’      const message = err instanceof Error ? err.message : "Failed to fetch vaults";
    47â†’      setError(message);
    48â†’    } finally {
    49â†’      setIsLoading(false);
    50â†’    }
    51â†’  }, [account?.address, currentVault]);
    52â†’
    53â†’  // Fetch vaults when account changes
    54â†’  useEffect(() => {
    55â†’    if (account?.address) {
    56â†’      fetchVaults();
    57â†’    } else {
    58â†’      setVaults([]);
    59â†’      setCurrentVault(null);
    60â†’    }
    61â†’  }, [account?.address, fetchVaults]);
    62â†’
    63â†’  // Create a new vault
    64â†’  const createVault = useCallback(async () => {
    65â†’    if (!account?.address) {
    66â†’      setError("Wallet not connected");
    67â†’      return null;
    68â†’    }
    69â†’
    70â†’    setIsLoading(true);
    71â†’    setError(null);
    72â†’
    73â†’    try {
    74â†’      const tx = buildCreateVaultTx();
    75â†’      const result = await signAndExecute({
    76â†’        transaction: tx,
    77â†’      });
    78â†’
    79â†’      // Refresh vaults after creation
    80â†’      await fetchVaults();
    81â†’
    82â†’      return result;
    83â†’    } catch (err) {
    84â†’      const message = err instanceof Error ? err.message : "Failed to create vault";
    85â†’      setError(message);
    86â†’      return null;
    87â†’    } finally {
    88â†’      setIsLoading(false);
    89â†’    }
    90â†’  }, [account?.address, signAndExecute, fetchVaults]);
    91â†’
    92â†’  // Store a blob reference
    93â†’  const storeBlob = useCallback(
    94â†’    async (blobId: Uint8Array, blobType: number, iv: Uint8Array) => {
    95â†’      if (!currentVault) {
    96â†’        setError("No vault selected");
    97â†’        return null;
    98â†’      }
    99â†’
   100â†’      setIsLoading(true);
   101â†’      setError(null);
   102â†’
   103â†’      try {
   104â†’        const tx = buildStoreBlobTx(currentVault.objectId, blobId, blobType, iv);
   105â†’        const result = await signAndExecute({
   106â†’          transaction: tx,
   107â†’        });
   108â†’
   109â†’        // Refresh vault details
   110â†’        const details = await getVaultDetails(currentVault.objectId);
   111â†’        if (details) {
   112â†’          setCurrentVault(details);
   113â†’        }
   114â†’
   115â†’        return result;
   116â†’      } catch (err) {
   117â†’        const message = err instanceof Error ? err.message : "Failed to store blob";
   118â†’        setError(message);
   119â†’        return null;
   120â†’      } finally {
   121â†’        setIsLoading(false);
   122â†’      }
   123â†’    },
   124â†’    [currentVault, signAndExecute]
   125â†’  );
   126â†’
   127â†’  // Grant access to a provider
   128â†’  const grantAccess = useCallback(
   129â†’    async (provider: string, scope: number, expiresAt: number = 0) => {
   130â†’      if (!currentVault) {
   131â†’        setError("No vault selected");
   132â†’        return null;
   133â†’      }
   134â†’
   135â†’      setIsLoading(true);
   136â†’      setError(null);
   137â†’
   138â†’      try {
   139â†’        const tx = buildGrantAccessTx(currentVault.objectId, provider, scope, expiresAt);
   140â†’        const result = await signAndExecute({
   141â†’          transaction: tx,
   142â†’        });
   143â†’
   144â†’        return result;
   145â†’      } catch (err) {
   146â†’        const message = err instanceof Error ? err.message : "Failed to grant access";
   147â†’        setError(message);
   148â†’        return null;
   149â†’      } finally {
   150â†’        setIsLoading(false);
   151â†’      }
   152â†’    },
   153â†’    [currentVault, signAndExecute]
   154â†’  );
   155â†’
   156â†’  // Revoke access from a provider
   157â†’  const revokeAccess = useCallback(
   158â†’    async (provider: string) => {
   159â†’      if (!currentVault) {
   160â†’        setError("No vault selected");
   161â†’        return null;
   162â†’      }
   163â†’
   164â†’      setIsLoading(true);
   165â†’      setError(null);
   166â†’
   167â†’      try {
   168â†’        const tx = buildRevokeAccessTx(currentVault.objectId, provider);
   169â†’        const result = await signAndExecute({
   170â†’          transaction: tx,
   171â†’        });
   172â†’
   173â†’        return result;
   174â†’      } catch (err) {
   175â†’        const message = err instanceof Error ? err.message : "Failed to revoke access";
   176â†’        setError(message);
   177â†’        return null;
   178â†’      } finally {
   179â†’        setIsLoading(false);
   180â†’      }
   181â†’    },
   182â†’    [currentVault, signAndExecute]
   183â†’  );
   184â†’
   185â†’  return {
   186â†’    vaults,
   187â†’    currentVault,
   188â†’    setCurrentVault,
   189â†’    isLoading,
   190â†’    error,
   191â†’    fetchVaults,
   192â†’    createVault,
   193â†’    storeBlob,
   194â†’    grantAccess,
   195â†’    revokeAccess,
   196â†’  };
   197â†’}
