     3â†’import { useState, useCallback, useEffect } from "react";
     4â†’import { getEncryptionService, EncryptionService } from "@/lib/encryption";
     5â†’import { useCurrentAccount, useSignPersonalMessage } from "@mysten/dapp-kit";
     6â†’
     7â†’export function useEncryption() {
     8â†’  const [isInitialized, setIsInitialized] = useState(false);
     9â†’  const [isInitializing, setIsInitializing] = useState(false);
    10â†’  const [error, setError] = useState<string | null>(null);
    11â†’
    12â†’  const account = useCurrentAccount();
    13â†’  const { mutateAsync: signPersonalMessage } = useSignPersonalMessage();
    14â†’
    15â†’  const encryptionService = getEncryptionService();
    16â†’
    17â†’  // Check initialization status
    18â†’  useEffect(() => {
    19â†’    setIsInitialized(encryptionService.isInitialized());
    20â†’  }, [encryptionService]);
    21â†’
    22â†’  // Initialize encryption when wallet connects
    23â†’  const initialize = useCallback(async () => {
    24â†’    if (!account) {
    25â†’      setError("Wallet not connected");
    26â†’      return false;
    27â†’    }
    28â†’
    29â†’    if (isInitialized) {
    30â†’      return true;
    31â†’    }
    32â†’
    33â†’    setIsInitializing(true);
    34â†’    setError(null);
    35â†’
    36â†’    try {
    37â†’      await encryptionService.deriveKeyFromWallet(async (message: Uint8Array) => {
    38â†’        const result = await signPersonalMessage({
    39â†’          message,
    40â†’        });
    41â†’        // Convert base64 signature to Uint8Array
    42â†’        const signature = Uint8Array.from(atob(result.signature), c => c.charCodeAt(0));
    43â†’        return { signature };
    44â†’      });
    45â†’
    46â†’      setIsInitialized(true);
    47â†’      return true;
    48â†’    } catch (err) {
    49â†’      const message = err instanceof Error ? err.message : "Failed to initialize encryption";
    50â†’      setError(message);
    51â†’      return false;
    52â†’    } finally {
    53â†’      setIsInitializing(false);
    54â†’    }
    55â†’  }, [account, isInitialized, encryptionService, signPersonalMessage]);
    56â†’
    57â†’  // Encrypt data
    58â†’  const encrypt = useCallback(
    59â†’    async (plaintext: string) => {
    60â†’      if (!isInitialized) {
    61â†’        const success = await initialize();
    62â†’        if (!success) {
    63â†’          throw new Error("Failed to initialize encryption");
    64â†’        }
    65â†’      }
    66â†’      return encryptionService.encrypt(plaintext);
    67â†’    },
    68â†’    [isInitialized, initialize, encryptionService]
    69â†’  );
    70â†’
    71â†’  // Decrypt data
    72â†’  const decrypt = useCallback(
    73â†’    async (ciphertext: Uint8Array, iv: Uint8Array) => {
    74â†’      if (!isInitialized) {
    75â†’        const success = await initialize();
    76â†’        if (!success) {
    77â†’          throw new Error("Failed to initialize encryption");
    78â†’        }
    79â†’      }
    80â†’      return encryptionService.decrypt(ciphertext, iv);
    81â†’    },
    82â†’    [isInitialized, initialize, encryptionService]
    83â†’  );
    84â†’
    85â†’  // Clear encryption key
    86â†’  const clear = useCallback(() => {
    87â†’    encryptionService.clear();
    88â†’    setIsInitialized(false);
    89â†’  }, [encryptionService]);
    90â†’
    91â†’  return {
    92â†’    isInitialized,
    93â†’    isInitializing,
    94â†’    error,
    95â†’    initialize,
    96â†’    encrypt,
    97â†’    decrypt,
    98â†’    clear,
    99â†’  };
   100â†’}
