    12â†’// Contract configuration - Update after deployment
    13â†’// IMPORTANT: Set NEXT_PUBLIC_PACKAGE_ID in Vercel environment variables
    14â†’const _packageId = process.env.NEXT_PUBLIC_PACKAGE_ID;
    15â†’
    16â†’if (!_packageId || _packageId === "0x0" || _packageId.length < 10) {
