    29â†’
    30â†’/**
    31â†’ * Build transaction to create a new DataVault
    32â†’ */
    33â†’export function buildCreateVaultTx(): Transaction {
    34â†’  const tx = new Transaction();
    35â†’
    36â†’  tx.moveCall({
    37â†’    target: `${PACKAGE_ID}::${MODULE_NAME}::create_vault`,
    38â†’    arguments: [tx.object("0x6")], // Clock object
    39â†’  });
    40â†’
    41â†’  return tx;
    42â†’}
    43â†’
    44â†’/**
    45â†’ * Build transaction to store a blob reference
