     1â†’/**
     2â†’ * Walrus Storage Service
     3â†’ *
     4â†’ * Handles encrypted blob storage on Walrus decentralized storage.
     5â†’ */
     6â†’
     7â†’// Walrus aggregator and publisher endpoints
     8â†’const WALRUS_AGGREGATOR = "https://aggregator.walrus-testnet.walrus.space";
     9â†’const WALRUS_PUBLISHER = "https://publisher.walrus-testnet.walrus.space";
    10â†’
    11â†’export interface WalrusUploadResult {
    12â†’  blobId: string;
    13â†’  endEpoch: number;
    14â†’}
    15â†’
    16â†’export interface WalrusBlob {
    17â†’  data: Uint8Array;
    18â†’}
    19â†’
    20â†’/**
    21â†’ * Upload encrypted data to Walrus
    22â†’ */
    23â†’export async function uploadToWalrus(
    24â†’  encryptedData: Uint8Array,
    25â†’  epochs: number = 5
    26â†’): Promise<WalrusUploadResult> {
    27â†’  // Create a proper ArrayBuffer for the body
    28â†’  const bodyBuffer = new ArrayBuffer(encryptedData.length);
    29â†’  new Uint8Array(bodyBuffer).set(encryptedData);
    30â†’
    31â†’  const response = await fetch(`${WALRUS_PUBLISHER}/v1/blobs?epochs=${epochs}`, {
    32â†’    method: "PUT",
    33â†’    headers: {
    34â†’      "Content-Type": "application/octet-stream",
    35â†’    },
    36â†’    body: bodyBuffer,
    37â†’  });
    38â†’
    39â†’  if (!response.ok) {
    40â†’    const error = await response.text();
    41â†’    throw new Error(`Failed to upload to Walrus: ${error}`);
    42â†’  }
    43â†’
    44â†’  const result = await response.json();
    45â†’
    46â†’  // Handle both newlyCreated and alreadyCertified responses
    47â†’  if (result.newlyCreated) {
    48â†’    return {
    49â†’      blobId: result.newlyCreated.blobObject.blobId,
    50â†’      endEpoch: result.newlyCreated.blobObject.storage.endEpoch,
    51â†’    };
    52â†’  } else if (result.alreadyCertified) {
    53â†’    return {
    54â†’      blobId: result.alreadyCertified.blobId,
    55â†’      endEpoch: result.alreadyCertified.endEpoch,
    56â†’    };
    57â†’  }
    58â†’
    59â†’  throw new Error("Unexpected Walrus response format");
    60â†’}
    61â†’
    62â†’/**
    63â†’ * Download encrypted data from Walrus
    64â†’ */
    65â†’export async function downloadFromWalrus(blobId: string): Promise<Uint8Array> {
    66â†’  const response = await fetch(`${WALRUS_AGGREGATOR}/v1/blobs/${blobId}`, {
    67â†’    method: "GET",
    68â†’  });
    69â†’
    70â†’  if (!response.ok) {
    71â†’    throw new Error(`Failed to download from Walrus: ${response.statusText}`);
    72â†’  }
    73â†’
    74â†’  const arrayBuffer = await response.arrayBuffer();
    75â†’  return new Uint8Array(arrayBuffer);
    76â†’}
    77â†’
    78â†’/**
    79â†’ * Check if a blob exists on Walrus
    80â†’ */
    81â†’export async function blobExists(blobId: string): Promise<boolean> {
    82â†’  try {
    83â†’    const response = await fetch(`${WALRUS_AGGREGATOR}/v1/blobs/${blobId}`, {
    84â†’      method: "HEAD",
    85â†’    });
    86â†’    return response.ok;
    87â†’  } catch {
    88â†’    return false;
    89â†’  }
    90â†’}
