     1â†’/**
     2â†’ * MindVault Encryption Service
     3â†’ *
     4â†’ * Client-side AES-256-GCM encryption with key derivation from wallet signature.
     5â†’ * The encryption key never leaves the browser - only the user can decrypt their data.
     6â†’ */
     7â†’
     8â†’export class EncryptionService {
     9â†’  private key: CryptoKey | null = null;
    10â†’  private initialized = false;
    11â†’
    12â†’  /**
    13â†’   * Derive encryption key from wallet signature
    14â†’   * Uses HKDF to derive a deterministic AES-256 key from the wallet's signature
    15â†’   */
    16â†’  async deriveKeyFromWallet(
    17â†’    signMessage: (message: Uint8Array) => Promise<{ signature: Uint8Array }>
    18â†’  ): Promise<void> {
    19â†’    // 1. Sign a fixed message to get deterministic key material
    20â†’    const message = new TextEncoder().encode("mindvault-encryption-key-v1");
    21â†’    const { signature } = await signMessage(message);
    22â†’
    23â†’    // 2. Import signature as key material for HKDF
    24â†’    // Create a new ArrayBuffer to ensure compatibility
    25â†’    const signatureBuffer = new ArrayBuffer(signature.length);
    26â†’    new Uint8Array(signatureBuffer).set(signature);
    27â†’
    28â†’    const keyMaterial = await crypto.subtle.importKey(
    29â†’      "raw",
    30â†’      signatureBuffer,
    31â†’      "HKDF",
    32â†’      false,
    33â†’      ["deriveKey"]
    34â†’    );
    35â†’
    36â†’    // 3. Derive AES-256-GCM key using HKDF
    37â†’    const saltBuffer = new ArrayBuffer(17);
    38â†’    new Uint8Array(saltBuffer).set(new TextEncoder().encode("mindvault-salt-v1"));
    39â†’
    40â†’    const infoBuffer = new ArrayBuffer(11);
    41â†’    new Uint8Array(infoBuffer).set(new TextEncoder().encode("aes-gcm-key"));
    42â†’
    43â†’    this.key = await crypto.subtle.deriveKey(
    44â†’      {
    45â†’        name: "HKDF",
    46â†’        hash: "SHA-256",
    47â†’        salt: saltBuffer,
    48â†’        info: infoBuffer,
    49â†’      },
    50â†’      keyMaterial,
    51â†’      { name: "AES-GCM", length: 256 },
    52â†’      false,
    53â†’      ["encrypt", "decrypt"]
    54â†’    );
    55â†’
    56â†’    this.initialized = true;
    57â†’  }
    58â†’
    59â†’  /**
    60â†’   * Check if encryption service is initialized
    61â†’   */
    62â†’  isInitialized(): boolean {
    63â†’    return this.initialized && this.key !== null;
    64â†’  }
    65â†’
    66â†’  /**
    67â†’   * Encrypt plaintext using AES-256-GCM
    68â†’   * Returns ciphertext and IV (initialization vector)
    69â†’   */
    70â†’  async encrypt(plaintext: string): Promise<{ ciphertext: Uint8Array; iv: Uint8Array }> {
    71â†’    if (!this.key) {
    72â†’      throw new Error("Encryption key not initialized. Call deriveKeyFromWallet first.");
    73â†’    }
    74â†’
    75â†’    // Generate random IV for each encryption
    76â†’    const ivBuffer = new ArrayBuffer(12);
    77â†’    const iv = new Uint8Array(ivBuffer);
    78â†’    crypto.getRandomValues(iv);
    79â†’
    80â†’    const encoded = new TextEncoder().encode(plaintext);
    81â†’    const encodedBuffer = new ArrayBuffer(encoded.length);
    82â†’    new Uint8Array(encodedBuffer).set(encoded);
    83â†’
    84â†’    const ciphertext = await crypto.subtle.encrypt(
    85â†’      { name: "AES-GCM", iv: ivBuffer },
    86â†’      this.key,
    87â†’      encodedBuffer
    88â†’    );
    89â†’
    90â†’    return {
    91â†’      ciphertext: new Uint8Array(ciphertext),
    92â†’      iv,
    93â†’    };
    94â†’  }
    95â†’
    96â†’  /**
    97â†’   * Decrypt ciphertext using AES-256-GCM
    98â†’   */
    99â†’  async decrypt(ciphertext: Uint8Array, iv: Uint8Array): Promise<string> {
   100â†’    if (!this.key) {
   101â†’      throw new Error("Encryption key not initialized. Call deriveKeyFromWallet first.");
   102â†’    }
   103â†’
   104â†’    // Create proper ArrayBuffers
   105â†’    const ivBuffer = new ArrayBuffer(iv.length);
   106â†’    new Uint8Array(ivBuffer).set(iv);
   107â†’
   108â†’    const ciphertextBuffer = new ArrayBuffer(ciphertext.length);
   109â†’    new Uint8Array(ciphertextBuffer).set(ciphertext);
   110â†’
   111â†’    const decrypted = await crypto.subtle.decrypt(
   112â†’      { name: "AES-GCM", iv: ivBuffer },
   113â†’      this.key,
   114â†’      ciphertextBuffer
   115â†’    );
   116â†’
   117â†’    return new TextDecoder().decode(decrypted);
   118â†’  }
   119â†’
   120â†’  /**
   121â†’   * Clear the encryption key from memory
   122â†’   */
   123â†’  clear(): void {
   124â†’    this.key = null;
   125â†’    this.initialized = false;
   126â†’  }
   127â†’}
   128â†’
   129â†’// Singleton instance
   130â†’let encryptionService: EncryptionService | null = null;
   131â†’
   132â†’export function getEncryptionService(): EncryptionService {
   133â†’  if (!encryptionService) {
   134â†’    encryptionService = new EncryptionService();
   135â†’  }
   136â†’  return encryptionService;
   137â†’}
   138â†’
   139â†’/**
   140â†’ * Utility: Convert Uint8Array to hex string
   141â†’ */
   142â†’export function toHex(bytes: Uint8Array): string {
   143â†’  return Array.from(bytes)
   144â†’    .map((b) => b.toString(16).padStart(2, "0"))
   145â†’    .join("");
   146â†’}
   147â†’
   148â†’/**
   149â†’ * Utility: Convert hex string to Uint8Array
   150â†’ */
   151â†’export function fromHex(hex: string): Uint8Array {
   152â†’  const bytes = new Uint8Array(hex.length / 2);
   153â†’  for (let i = 0; i < hex.length; i += 2) {
   154â†’    bytes[i / 2] = parseInt(hex.substr(i, 2), 16);
   155â†’  }
   156â†’  return bytes;
   157â†’}
   158â†’
   159â†’/**
   160â†’ * Utility: Convert Uint8Array to base64
   161â†’ */
   162â†’export function toBase64(bytes: Uint8Array): string {
   163â†’  return btoa(String.fromCharCode(...bytes));
   164â†’}
   165â†’
   166â†’/**
   167â†’ * Utility: Convert base64 to Uint8Array
   168â†’ */
   169â†’export function fromBase64(base64: string): Uint8Array {
   170â†’  const binary = atob(base64);
   171â†’  const bytes = new Uint8Array(binary.length);
   172â†’  for (let i = 0; i < binary.length; i++) {
   173â†’    bytes[i] = binary.charCodeAt(i);
   174â†’  }
   175â†’  return bytes;
   176â†’}
