   295â†’                {message.encrypted && (
   296â†’                  <span className="flex items-center gap-1">
   297â†’                    <Lock className="w-3 h-3" />
   298â†’                    Encrypted
   299â†’                  </span>
   300â†’                )}
   301â†’              </div>
   302â†’            </div>
   303â†’
   304â†’            {message.role === "user" && (
