   306â†’                <User className="w-5 h-5 text-white" />
   307â†’              </div>
   308â†’            )}
   309â†’          </div>
   310â†’        ))}
   311â†’
   312â†’        {isLoading && (
   313â†’          <div className="flex gap-3">
   314â†’            <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center">
   315â†’              <Bot className="w-5 h-5 text-white" />
   316â†’            </div>
   317â†’            <div className="bg-gray-800 rounded-2xl px-4 py-3">
   318â†’              <Loader2 className="w-5 h-5 animate-spin text-gray-400" />
   319â†’            </div>
   320â†’          </div>
   321â†’        )}
   322â†’
   323â†’        <div ref={messagesEndRef} />
   324â†’      </div>
   325â†’
   326â†’      {/* Input */}
   327â†’      <form onSubmit={handleSubmit} className="p-4 border-t border-gray-800">
   328â†’        <div className="flex gap-3">
   329â†’          <input
   330â†’            type="text"
   331â†’            value={input}
   332â†’            onChange={(e) => setInput(e.target.value)}
   333â†’            placeholder={providerConfig ? "Type your message..." : "Configure AI provider in Settings first..."}
   334â†’            className="flex-1 bg-gray-800 border border-gray-700 rounded-xl px-4 py-3 text-white placeholder-gray-500 focus:outline-none focus:border-blue-500 transition-colors"
