   144â†’    // Delete Blob Reference
   145â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   146â†’    public entry fun delete_blob(
   147â†’        vault: &mut DataVault,
   148â†’        index: u64,
   149â†’        _ctx: &mut TxContext
   150â†’    ) {
   151â†’        let blob_key = BlobKey { index };
   152â†’        assert!(dynamic_field::exists_(&vault.id, blob_key), EBlobNotFound);
   153â†’
   154â†’        let _: BlobRef = dynamic_field::remove(&mut vault.id, blob_key);
   155â†’
   156â†’        event::emit(BlobDeleted {
   157â†’            vault_id: object::id(vault),
   158â†’            index,
   159â†’        });
   160â†’    }
   161â†’
   162â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   163â†’    // Grant Access - Add authorization record inside Vault (no object transfer!)
   164â†’    // Key Fix: Authorization data stored as Dynamic Field in user's Vault
   165â†’    //          User always owns Vault, so can always revoke authorization
   166â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   167â†’    public entry fun grant_access(
   168â†’        vault: &mut DataVault,
   169â†’        provider: address,
   170â†’        scope: u8,
   171â†’        expires_at: u64,
   172â†’        clock: &Clock,
   173â†’        _ctx: &mut TxContext
   174â†’    ) {
   175â†’        assert!(scope == SCOPE_READ || scope == SCOPE_READ_WRITE, EInvalidScope);
   176â†’
   177â†’        let auth_key = AuthKey { provider };
   178â†’
   179â†’        // If authorization already exists, remove it first
   180â†’        if (dynamic_field::exists_(&vault.id, auth_key)) {
   181â†’            let _: Authorization = dynamic_field::remove(&mut vault.id, auth_key);
   182â†’        };
   183â†’
   184â†’        let auth = Authorization {
   185â†’            provider,
   186â†’            scope,
   187â†’            expires_at,
   188â†’            created_at: clock::timestamp_ms(clock),
   189â†’        };
   190â†’
   191â†’        dynamic_field::add(&mut vault.id, auth_key, auth);
   192â†’
   193â†’        event::emit(AccessGranted {
   194â†’            vault_id: object::id(vault),
   195â†’            provider,
   196â†’            scope,
   197â†’        });
   198â†’    }
   199â†’
   200â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   201â†’    // Revoke Access - User can revoke anytime since auth data is in their Vault
   202â†’    // Key Fix: User operates on their own Vault, no Provider cooperation needed
   203â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   204â†’    public entry fun revoke_access(
   205â†’        vault: &mut DataVault,
   206â†’        provider: address,
   207â†’        _ctx: &mut TxContext
   208â†’    ) {
   209â†’        let auth_key = AuthKey { provider };
   210â†’
   211â†’        assert!(dynamic_field::exists_(&vault.id, auth_key), ENotAuthorized);
   212â†’
   213â†’        let _: Authorization = dynamic_field::remove(&mut vault.id, auth_key);
   214â†’
   215â†’        event::emit(AccessRevoked {
   216â†’            vault_id: object::id(vault),
   217â†’            provider,
   218â†’        });
   219â†’    }
   220â†’
   221â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   222â†’    // Verify Access Permission - View function for backend to call
   223â†’    // Provider queries Vault's Dynamic Field to verify if they're authorized
   224â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   225â†’    public fun is_authorized(
   226â†’        vault: &DataVault,
   227â†’        provider: address,
   228â†’        clock: &Clock
   229â†’    ): bool {
   230â†’        let auth_key = AuthKey { provider };
   231â†’
   232â†’        if (!dynamic_field::exists_(&vault.id, auth_key)) {
   233â†’            return false
   234â†’        };
   235â†’
   236â†’        let auth: &Authorization = dynamic_field::borrow(&vault.id, auth_key);
   237â†’
   238â†’        // Check if expired
   239â†’        auth.expires_at == 0 || auth.expires_at > clock::timestamp_ms(clock)
   240â†’    }
   241â†’
   242â†’    // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
