    30â†’/**
    31â†’ * Build transaction to create a new DataVault
    32â†’ */
    33â†’export function buildCreateVaultTx(): Transaction {
    34â†’  const tx = new Transaction();
    35â†’
    36â†’  tx.moveCall({
    37â†’    target: `${PACKAGE_ID}::${MODULE_NAME}::create_vault`,
    38â†’    arguments: [tx.object("0x6")], // Clock object
    39â†’  });
    40â†’
    41â†’  return tx;
    42â†’}
    43â†’
    44â†’/**
    45â†’ * Build transaction to store a blob reference
    46â†’ */
    47â†’export function buildStoreBlobTx(
    48â†’  vaultId: string,
    49â†’  blobId: Uint8Array,
    50â†’  blobType: number,
    51â†’  iv: Uint8Array
    52â†’): Transaction {
    53â†’  const tx = new Transaction();
    54â†’
    55â†’  tx.moveCall({
    56â†’    target: `${PACKAGE_ID}::${MODULE_NAME}::store_blob`,
    57â†’    arguments: [
    58â†’      tx.object(vaultId),
    59â†’      tx.pure.vector("u8", Array.from(blobId)),
    60â†’      tx.pure.u8(blobType),
    61â†’      tx.pure.vector("u8", Array.from(iv)),
    62â†’      tx.object("0x6"), // Clock object
    63â†’    ],
    64â†’  });
    65â†’
    66â†’  return tx;
    67â†’}
    68â†’
    69â†’/**
    70â†’ * Build transaction to grant access to a provider
    71â†’ */
    72â†’export function buildGrantAccessTx(
    73â†’  vaultId: string,
    74â†’  provider: string,
    75â†’  scope: number,
    76â†’  expiresAt: number
    77â†’): Transaction {
    78â†’  const tx = new Transaction();
    79â†’
    80â†’  tx.moveCall({
    81â†’    target: `${PACKAGE_ID}::${MODULE_NAME}::grant_access`,
    82â†’    arguments: [
    83â†’      tx.object(vaultId),
    84â†’      tx.pure.address(provider),
    85â†’      tx.pure.u8(scope),
    86â†’      tx.pure.u64(expiresAt),
    87â†’      tx.object("0x6"), // Clock object
    88â†’    ],
    89â†’  });
    90â†’
    91â†’  return tx;
    92â†’}
