        return messages;
      } catch (err) {
        console.error("Failed to load history:", err);
        return [];
      }
    },
    [currentVault]
  );

  // Get blob references (for proof generation)
  const getBlobRefs = useCallback(async (): Promise<BlobRefData[]> => {
    if (!currentVault) {
      return [];
    }
    return getAllBlobRefs(currentVault.objectId);
  }, [currentVault]);

  return {
    vaults,
    currentVault,
    setCurrentVault,
    isLoading,
    error,
    fetchVaults,
    createVault,
    storeBlob,
    grantAccess,
    revokeAccess,
    loadHistory,
    getBlobRefs,
  };
}

